INSERT INTO characters (id, name, role, actor_name, photo, appearance_season_episode, description) VALUES
(1, 'Arthur Pendragon', 'Principal', 'Alexandre Astier', '/images/characters/arthurpendragon.webp', 'S01E01', 'Le roi Arthur, le héros de la série, un peu perdu dans son rôle.'),
(2, 'Lancelot du Lac', 'Principal', 'Thomas Cousseau', '/images/characters/lancelotdulac.webp', 'S01E02', 'Un chevalier complexe, parfois rival d''Arthur.'),
(3, 'Perceval', 'Principal', 'Jean-Christophe Hembert', '/images/characters/perceval.webp', 'S01E01', 'Un chevalier loyal mais parfois naïf et maladroit.'),
(4, 'Karadoc', 'Principal', 'Serge Papagalli', '/images/characters/karadoc.webp', 'S01E01', 'Un chevalier aimant les bonnes choses de la vie, surtout la nourriture.'),
(5, 'Gauvain', 'Principal', 'François Rollin', '/images/characters/gauvain.webp', 'S01E01', 'Un chevalier sage et sérieux, souvent dans l''ombre d''Arthur.'),
(6, 'Le Roi Burgonde', 'Secondaire', 'Guillaume Briat', '/images/characters/roiburgonde.webp', 'S01E24', 'Roi stupide et grossier, connu pour sa réplique "Arthur ! Cuillère !" et son incompréhension de la langue de Kaamelott.'),
(7, 'Bohort', 'Principal', 'Loup-Denis Elion', '/images/characters/bohort.webp', 'S01E01', 'Un chevalier craintif, mais attaché à l''honneur.'),
(8, 'Merlin', 'Principal', 'Jacques Chambon', '/images/characters/merlin.webp', 'S01E01', 'Le magicien un peu décalé et toujours en quête de savoir.'),
(9, 'Le Père Blaise', 'Principal', 'Alexandre Astier', '/images/characters/lepereblaise.webp', 'S01E15', 'Un moine naïf, parfois aveugle aux réalités du monde.'),
(10, 'Le Traducteur du Roi Burgonde', 'Secondaire', 'Loránt Deutsch', '/images/characters/traducteur.webp', 'S01E24', 'Interprète du Roi Burgonde, tente de traduire ses propos incompréhensibles lors des négociations avec Arthur.'),
(12, 'Guenièvre', 'Principal', 'Audrey Fleurot', '/images/characters/guenievre.webp', 'S01E01', 'La reine Guenièvre, épouse d''Arthur, qui cache une certaine complicité avec Lancelot.'),
(14, 'Yvain', 'Principal', 'François Levantal', '/images/characters/yvain.webp', 'S02E08', 'Un chevalier loyal qui aide Arthur dans plusieurs épisodes.'),
(16, 'Léodagan', 'Principal', 'Lionnel Astier', '/images/characters/leodagan.webp', 'S01E03', 'Le père de Guenièvre et un puissant seigneur.'),
(17, 'Roparzh', 'Secondaire', 'Véronique Augereau', '/images/characters/roparzh.webp', 'S02E01', 'Un membre de l''entourage de Léodagan, souvent sarcastique.'),
(21, 'Dame Séli', 'Principal', 'Joëlle Sevilla', '/images/characters/dameseli.webp', 'S01E03', 'Mère de Guenièvre et épouse de Léodagan, connue pour son caractère fort et ses remarques acerbes.'),
(22, 'Elias de kelliwich', 'Secondaire', 'Bruno Fontaine', '/images/characters/eliasdekelliwich.webp', 'S01E39', 'Enchanteur rival de Merlin, souvent appelé pour ses compétences supérieures en magie.'),
(23, 'Venec', 'Secondaire', 'Loïc Varraut', '/images/characters/venec.webp', 'S01E16', 'Marchand d''esclaves et homme à tout faire, toujours prêt à faire des affaires douteuses.'),
(24, 'Kadoc', 'Secondaire', 'Brice Fournier', '/images/characters/kadoc.webp', 'S02E60', 'Frère de Karadoc, connu pour ses phrases absurdes et son comportement étrange.'),
(25, 'Demetra', 'Secondaire', 'Caroline Ferrus', '/images/characters/demetra.webp', 'S01E98', 'Une des maîtresses du roi Arthur, apparaissant souvent dans ses rêves.'),
(26, 'Angharad', 'Secondaire', 'Vanessa Guedj', '/images/characters/angharad.webp', 'S01E04', 'Servante de Guenièvre, souvent impliquée dans les intrigues de la cour.'),
(27, 'Le Roi Loth', 'Secondaire', 'François Rollin', '/images/characters/leroiloth.webp', 'S01E28', 'Roi d''Orcanie, beau-frère d''Arthur, connu pour ses monologues philosophiques et ses complots.'),
(28, 'Séfriane d''Aquitaine', 'Secondaire', 'Émilie Dequenne', '/images/characters/sefrianedaquitaine.webp', 'S03E40', 'Princesse d''Aquitaine, brièvement fiancée à Arthur.'),
(29, 'Mevanwi', 'Secondaire', 'Caroline Pascal', '/images/characters/mevanwi.webp', 'S02E44', 'Épouse de Karadoc, impliquée dans une relation adultère avec Arthur.'),
(30, 'Calogrenant', 'Secondaire', 'Stéphane Margot', '/images/characters/calogrenant.webp', 'S01E48', 'Roi de Calédonie, un des chevaliers de la Table Ronde.'),
(31, 'Dagonet', 'Secondaire', 'Antoine de Caunes', '/images/characters/dagonet.webp', 'S01E07', 'Chevalier de la Table Ronde, connu pour sa naïveté et sa maladresse.'),
(32, 'La Dame du Lac', 'Principal', 'Audrey Fleurot', '/images/characters/ladamedulac.webp', 'S01E01', 'Émissaire des dieux, visible uniquement par Arthur, elle le guide dans sa quête du Graal.'),
(33, 'Attila', 'Secondaire', 'Guillaume Briat', '/images/characters/attila.webp', 'S02E01', 'Chef des Huns, ennemi redoutable d''Arthur et de Kaamelott.'),
(34, 'Manius Macrinus Firmus', 'Guest', 'Tchéky Karyo', '/images/characters/maniusmacrinusfirmus.webp', 'S03E01', 'Général romain, ancien mentor d''Arthur et ennemi occasionnel de Kaamelott.'),
(35, 'Méléagant', 'Antagoniste', 'Carlo Brandt', '/images/characters/meleagant.webp', 'S05E01', 'Mystérieux personnage aux pouvoirs surnaturels, principal antagoniste de la saison 5.'),
(36, 'Verinus', 'Secondaire', 'François Morel', '/images/characters/verinus.webp', 'S01E26', 'Traducteur officiel de la cour de Kaamelott, souvent sollicité pour des missions diplomatiques.'),
(37, 'Goustan le Cruel', 'Guest', 'Yves Gaudin', '/images/characters/goustanlecruel.webp', 'S02E42', 'Père de Léodagan et grand-père de Yvain, connu pour sa cruauté.'),
(38, 'Ferghus', 'Secondaire', 'Loïc Varraut', '/images/characters/ferghus.webp', 'S01E22', 'Chef Picte, souvent en conflit avec le royaume de Logres.'),
(39, 'Caius Camillus', 'Guest', 'Bruno Salomone', '/images/characters/caiuscamillus.webp', 'S02E07', 'Centurion romain, ami d''Arthur et espion à la solde de Rome.'),
(40, 'Appius Manilius', 'Guest', 'Christian Bujeau', '/images/characters/appiusmanilius.webp', 'S03E26', 'Meilleur ami d''Arturus, Appius Manilius est un soldat de la milice urbaine romaine.'),
(41, 'Ygerne de Tintagel', 'Secondaire', 'Josée Drevon', '/images/characters/ygernedetintagel.webp', 'S03E01', 'Mère biologique d''Arthur, reine de Tintagel. Femme autoritaire et manipulatrice, elle entretient une relation complexe avec son fils.');